import logo from './logo.svg';
import './App.css';
import axios from 'axios';
import Web3 from 'web3';
import QuarkChain from 'quarkchain-web3';

//gobal variables
let web3;
let address;

const poolContractAddress = "0x006631C067D34928F55520511f01A5aBfA0AC67a";
const fullShardKey = "0003c66C"
const jrpcUrl = "http://jrpc.mainnet.quarkchain.io:38391";

if(window.ethereum){
  web3 = new Web3(window.ethereum);
  QuarkChain.injectWeb3(web3, jrpcUrl);

  window.ethereum.enable();
  setAddress();
}

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <h2>QKC Chain3 staking pool</h2>
            <p>
              <table>
                  <tr>
                      <th>Function</th>
                      <th>Output</th>
                  </tr>
                  <tr>
                    <td> Your Address</td>
                    <td>{address}</td>
                  </tr>
                  <tr>
                      <td>Total stakes</td>
                      <td>{getBalance()}</td>
                  </tr>
                  <tr>
                      <td>Miner fee</td>
                      <td id="minerFee"></td>
                  </tr>
                  <tr>
                      <td>Pool fee</td>
                      <td id="poolFee"></td>
                  </tr>
                  <tr>
                      <td>Minimum stake</td>
                      <td id="minStake"></td>
                  </tr>
                  <tr>
                      <td>Pool status</td>
                      <td id="status"></td>
                  </tr>
                  <tr>
                      <td>Deposit</td>
                      <td></td>
                      <td id="deposit"></td>
                  </tr>
              </table>
            </p>
      </header>
    </div>
  );
}

function sendTransaction(address, value) {
  let rawTx = {
    to: poolContractAddress,
    fromFullShardKey: "0x"+fullShardKey,
    toFullShardKey: "0x"+fullShardKey,
    gas: String(500000).toString("hex"),
    gasPrice: String
  }

}

function getBalance() {
  web3.qkc.getBalance(poolContractAddress + fullShardKey, function(err, res) {
    console.log(res.c[1])
    return res/10**18
  });
}

function setAddress() {
  let address1 = web3.eth.accounts[0];
  address = address1;
  console.log(address)
}

export default App;
