import './App.css';
import axios from 'axios';
import Web3 from 'web3';
import QuarkChain from 'quarkchain-web3';
import Async from 'react-async';
//import ABIinterface from './components/config';
import poolOutput from './components/web3Component'
import React from 'react';

const ABIinterface = [{"inputs":[],"stateMutability":"nonpayable","type":"constructor"},{"inputs":[{"internalType":"uint256","name":"_minStake","type":"uint256"}],"name":"adjustMinStake","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"uint16","name":"amount","type":"uint16"}],"name":"adjustMinerFee","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"uint16","name":"amount","type":"uint16"}],"name":"adjustPoolFee","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"address payable","name":"_miner","type":"address"}],"name":"changeMiner","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"bool","name":"status","type":"bool"}],"name":"changePoolStatus","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[],"name":"flushContract","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"address","name":"staker","type":"address"}],"name":"getStakesWithRewards","outputs":[{"internalType":"uint256","name":"","type":"uint256"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"isClosed","outputs":[{"internalType":"bool","name":"","type":"bool"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"minStake","outputs":[{"internalType":"uint256","name":"","type":"uint256"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"miner","outputs":[{"internalType":"address payable","name":"","type":"address"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"minerFee","outputs":[{"internalType":"uint16","name":"","type":"uint16"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"owner","outputs":[{"internalType":"address payable","name":"","type":"address"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"poolFee","outputs":[{"internalType":"uint16","name":"","type":"uint16"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"totalStakes","outputs":[{"internalType":"uint256","name":"","type":"uint256"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"uint256","name":"amount","type":"uint256"}],"name":"withdrawStakes","outputs":[],"stateMutability":"nonpayable","type":"function"},{"stateMutability":"payable","type":"receive"}]
const roiABIinterface = [{"inputs":[],"stateMutability":"nonpayable","type":"constructor"},{"inputs":[{"internalType":"uint16","name":"snapshotNumber","type":"uint16"}],"name":"calculateROI","outputs":[{"internalType":"uint256","name":"","type":"uint256"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"uint256","name":"","type":"uint256"}],"name":"snapshotBalance","outputs":[{"internalType":"uint256","name":"","type":"uint256"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"uint16","name":"snapshotNumber","type":"uint16"}],"name":"takeNewSnapshot","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[],"name":"timestamp1","outputs":[{"internalType":"uint256","name":"","type":"uint256"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"timestamp2","outputs":[{"internalType":"uint256","name":"","type":"uint256"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"timestamp3","outputs":[{"internalType":"uint256","name":"","type":"uint256"}],"stateMutability":"view","type":"function"}]
const poolROIContractAddress = ["0xd00204F286548579eaE01357B22996B44f4b0747", "0x035f71F9e800271A9e2A2ec2a45eEAFBb298293C"];
const poolContractAddress = ["0x0", "0x0","0x94f151BBD12854f93cf92c882B88E3790F407956", "0x006631C067D34928F55520511f01A5aBfA0AC67a", "0x0", "0x0", "0x0", "0x0"];
const fullShardKey = ["0000C66c", "0001C66c", "0002C66c", "0003C66c", "0004C66c", "0005C66c", "0006C66c", "0007C66c"];
const jrpcUrl = "http://jrpc.mainnet.quarkchain.io:38391";
const standardShardKeys = ["00000000", "00010000", "00020000", "00030000", "00040000", "00050000", "00060000", "00070000"]

let address;

let web3 = new Web3()


if(window.ethereum){
  web3 = new Web3(web3.givenProvider);
  QuarkChain.injectWeb3(web3, jrpcUrl);
  window.ethereum.enable();
  address = web3.currentProvider.address;
}
console.log("test")

function Sleep(milliseconds) {
  return new Promise(resolve => setTimeout(resolve(100), milliseconds));
}

function App() {
  //console.log(getBalance(address + standardShardKeys[0]))

  return (
      <div className="App">
        <header className="App-header">
          <h2>QKC STAKING POOLS</h2>
          <p>{address}</p>
          <Balance address={poolContractAddress[2]+fullShardKey[2]} />
        </header>
      </div>
  );
}

function sendTransaction(address, value) {
  let rawTx = {
    to: poolContractAddress,
    fromFullShardKey: "0x"+fullShardKey,
    toFullShardKey: "0x"+fullShardKey,
    gas: String(500000).toString("hex"),
    gasPrice: String
  }

}

class Balance extends React.Component {
  Sleep(milliseconds) {
    return new Promise(resolve => setTimeout(resolve(), milliseconds));
  }



  render() {
    let result;
    web3.qkc.getBalance(this.props.address, function(err, res) {
      result = Number((res/10**18).toFixed(2)).toLocaleString
    });
    this.Sleep(10000)
    console.log(result)
    return (
      <h1>Total Stake: {result}</h1>
    )
  }
}


function GetBalance() {
  web3.qkc.getBalance(this.props.address, function(err, res) {
    console.log(res/10**18)
    return (
      <tr>
        <td>Total Stake</td>
        <td>{Number((res/10**18).toFixed(2)).toLocaleString}</td>
      </tr>
    )
  });
}


export default App;
