import Web3 from 'web3';
import QuarkChain from 'quarkchain-web3';
import React from 'react';
import {ABIinterface, roiABIinterface, poolROIContractAddress, poolContractAddress, fullShardKey, jrpcUrl, standardShardKeys} from './config.js'

let web3 = new Web3();
QuarkChain.injectWeb3(web3, jrpcUrl)

async function getContractInformation(poolAddress, poolROIAddress) {
  const roiContract = web3.qkc.contract(roiABIinterface).at(poolROIAddress);
  const poolContract = web3.qkc.contract(ABIinterface).at(poolAddress);
  let minerFee;
  let poolFee;

  let v1;
  let v2;
  let v3;
  let v4;
  let v5;
  let v6;
  let v7;

  web3.qkc.getBalance(poolAddress, function(err, res) {
    v1 = Number((res/10**18).toFixed(2)).toLocaleString() + " QKC";
  });

  poolContract.minerFee.call(function(err, res){
    minerFee = res/10000
    v2 = res/100+"%";
  });
  poolContract.poolFee.call(function(err, res){
    poolFee = res/10000
    v3 = res/100+"%";
  });
  poolContract.minStake.call(function(err, res){
    v4 = Number((res/10**18).toFixed(2)).toLocaleString() + " QKC";
  });

  poolContract.isClosed.call(function(err, res){
      if(res == false){
        v5 = "Open";
      }
      if(res == true){  
        v5 = "Closed";
      }
  });

  roiContract.calculateROI(1, {}, function(err, res) {
    let result = ((res/10**16)*(1-(minerFee + poolFee))).toFixed(3)
    v6 = result + "% / " + (result*12).toFixed(1) + "%";
  });

  return new Promise((resolve) => {
    setTimeout(() => {
      resolve({
        balance: v1,
        minerFee: v2,
        poolFee: v3,
        minStake: v4,
        status: v5,
        roi: v6,
        userStake: v7
      })
    }, 300)
  })
}

async function getUserInformation(web3) {
  let ad;
  let balance = [];

  if(web3.currentProvider.isQpocket){
    ad = web3.currentProvider.address;
  } else {
    ad = await web3.eth.getAccounts()[0]
  }
  for(let i=0; i<8; i++) {
    balance[i] += await web3.qkc.getBalance(ad + standardShardKeys[i])
  }

  return new Promise((resolve) => {
    setTimeout(() => {
      resolve({
        address: ad,
        balance: balance,
      })
    }, 1000)
  })
}

class PoolInterface extends React.Component {
  // poolAddress, poolROIAddress, index, miner, web3

  state = {
    balance : '',
    roi : '',
    minStake : '',
    poolStatus : ''
  }

  componentDidMount(props) {
    getContractInformation(this.props.poolAddress, this.props.poolROIAddress).then(data => {
      this.setState({balance: data.balance, roi: data.roi, minStake: data.minStake, poolStatus: data.status});
    });
    console.log(this.props)
  }

  render() {
    
    return (
      <table>
        <thead>
          <tr>
                <th colSpan="2">
                  <a href={"https://mainnet.quarkchain.io/address/" + this.props.poolAddress} target="_blank">Chain {this.props.index} Pool</a>
                </th>
          </tr>
        </thead>
        <tbody>
          <tr>
                <td>Estimated ROI monthly/annual</td>
                <td>{this.state.roi}</td>
            </tr>
            <tr>
                <td>Total Stakes</td>
                <td>{this.state.balance}</td>
            </tr>
            <tr>
                <td>Minimum Stake</td>
                <td>{this.state.minStake}</td>
            </tr>
            <tr>
                <td>Pool Status</td>
                <td>{this.state.poolStatus}</td>
            </tr>
            <tr>
              <td>Miner</td>
              <td>{this.props.miner}</td>
            </tr>
        </tbody>
      </table>
    )
  }
}

class poolUserInterface extends React.Component {
  render() {
    return (
      <a></a>
    )
  }
}

class PoolAllocateWithdraw extends React.Component {
  //poolAddress, web3

  state = {
    add: '',
    withdraw: ''
  }

  componentDidMount(props) {

  }

  sendTransaction() {
    let rawTx = {
      value: this.state.add*10**18,
      to: this.props.poolAddress,
      fromFullShardKey: QuarkChain.getFullShardKeyFromQkcAddress(this.props.poolAddress),
      toFullShardKey: QuarkChain.getFullShardKeyFromQkcAddress(this.props.poolAddress),
      gas: 350000,
      gasPrice: 2
    }
    
    //workaround mit ethers und axios notwendig
    this.props.web3.qkc.sendTransaction(rawTx, function(err, transactionId){
      console.log(transactionId)
    });
  }

  withdrawStake() {
    let rawTx = {
      value: this.state.withdraw*10**18,
      fromFullShardKey: QuarkChain.getFullShardKeyFromQkcAddress(this.props.poolAddress),
      toFullShardKey: QuarkChain.getFullShardKeyFromQkcAddress(this.props.poolAddress),
      gas: 350000,
      gasPrice: 2
    }
  }

  handleAddChange(event) {
    this.setState({add: event.target.value});
  }
  handleWithdrawChange(event) {
    this.setState({withdraw: event.target.value});
  }
  
  render() {
    if(window.ethereum) {
      return (
        <table>
          <tbody>
            <tr>
              <td>Add Stake</td>
              <td>
                <input type="number" min="0" value={this.state.value} onChange={e => this.handleAddChange(e)} />
              </td>
              <td>
                <button onClick={ () => this.sendTransaction}>Submit Tx</button>
              </td>
            </tr>
            <tr>
              <td>Withdraw</td>
              <td>
                <input type="number" min="0" value={this.state.value} onChange={e => this.handleWithdrawChange(e)} />
              </td>
              <td>
                <button onClick={ () => this.withdrawStake}>Submit Tx</button>
              </td>
            </tr>
          </tbody>
        </table>
      )
    } else {
      return (
        <table>
          <tfoot>
            Please make sure that you have a web3 wallet installed in your browser
          </tfoot>
        </table>
      )
    }
  }
}

export {PoolInterface, PoolAllocateWithdraw};
