import './App.css';
import Web3 from 'web3';
import QuarkChain from 'quarkchain-web3';
import {ABIinterface, roiABIinterface, poolROIContractAddress, poolContractAddress, fullShardKey, jrpcUrl, standardShardKeys} from './components/config';
import {PoolInterface, Navbar} from './components/poolInterface.js';
import React from 'react';
import {Route, BrowserRouter as Router, Switch} from 'react-router-dom';
import {ToastContainer} from 'react-toastify';

import 'bootstrap/dist/css/bootstrap.min.css';
import 'react-toastify/dist/ReactToastify.css';

import Home from './routes/Home';
import Miner from './routes/Miner';
import ROICalculator from './routes/ROICalculator'

let web3 = new Web3()
QuarkChain.injectWeb3(web3, jrpcUrl);




function App(route) {
  if(window.ethereum){
    web3 = new Web3(web3.givenProvider);
    QuarkChain.injectWeb3(web3, jrpcUrl);
    window.ethereum.enable();
  }
  if(route == "/"){
    return (
      <Router>
        <div className="App">
          <header className="App-header">
              <Navbar/>
              <ToastContainer/>
          </header>
           <Home web3={web3}/>
        </div>
      </Router>
    );
  }
  if(route == "/managepools"){
    return (
      <Router>
        <div className="App">
          <header className="App-header">
              <Navbar/>
              <ToastContainer/>
          </header>
           <Miner web3={web3}/>
        </div>
      </Router>
    );
  }
  if(route == "/roicalc"){
    return (
      <Router>
        <div className="App">
          <header className="App-header">
              <Navbar/>
              <ToastContainer/>
          </header>
           <ROICalculator/>
        </div>
      </Router>
    );
  }
}

export default App;
