import './App.css';
import axios from 'axios';
import Web3 from 'web3';
import QuarkChain from 'quarkchain-web3';
import Async from 'react-async';
//import Aync from 'react-async';

let web3;
let address;

const poolContractAddress = "0x006631C067D34928F55520511f01A5aBfA0AC67a";
const fullShardKey = "0003c66C"
const jrpcUrl = "http://jrpc.mainnet.quarkchain.io:38391";

if(window.ethereum){
  web3 = new Web3(window.ethereum);
  QuarkChain.injectWeb3(web3, jrpcUrl);
  window.ethereum.enable();
  getUserInformation().address.then(data => console.log(data))
}

const userInfo = () => (
  <Async promiseFn={getUserInformation()}>
    <Async.Resolved>
      {data => (
        <p>{data.address}</p>
      )}
    </Async.Resolved>
  </Async>
)

function App() {
  let address;
  let balance;

  return (
      <div className="App">
        <header className="App-header">
          <h2>QKC Chain3 staking pool</h2>
          Address: {userInfo}
        </header>
      </div>
  );
}

function sendTransaction(address, value) {
  let rawTx = {
    to: poolContractAddress,
    fromFullShardKey: "0x"+fullShardKey,
    toFullShardKey: "0x"+fullShardKey,
    gas: String(500000).toString("hex"),
    gasPrice: String
  }

}

function getBalance() {
  web3.qkc.getBalance(poolContractAddress + fullShardKey, function(err, res) {
    console.log(res.c[1])
    return res/10**18
  });
}
async function getContractInformation(address) {
  let contract = web3.qkc.contract(ABIinterface).at(address);
  return new Promise((resolve) => {
    setTimeout(() => {
      resolve({
        balance: web3.qkc.getBalance(address, function(err, res) {
          console.log(res.c[1])
          return res/10**18
        }),
        status: contract.isClosed.call(function(err, res){
          if(res == false){
            return"Open";
          }
          if(res == true){
            return "Closed";
          }
        })

      })
    }, 10000)
  })
}

async function getUserInformation() {
  return new Promise((resolve) => {
    setTimeout(() => {
      resolve({
        address: web3.eth.getAccounts()[0]
      })
    }, 10000)
  })
}

export default App;
