import axios from 'axios';
import React from 'react';

export default class ROICalculatorComp extends React.Component {

    state={
        chain2value: '',
        chain3value: '',
        chain7value: '',

        amount: 0,
    }

    componentDidMount() {
    }

    calculateReward(chainId, amount) {
        axios.post("http://localhost:3001/api/getLatestROI", {chainId}).then((response)=>{
            const data = response.data;
            return (data.roi/100)*amount
        });
    }

    handleChange(event) {
        this.setState({chain2value: this.calculateReward(2, event.target.value), chain3value: this.calculateReward(3, event.target.value)})
    }

    render() {
        return(
            <table>
                <tbody>
                    <tr>
                        <td colSpan="3">ROI Calculator</td>
                    </tr>
                    <tr>
                        <td colSpan="3"><input className="inp" onChange={e => this.handleChange(e)}/></td>
                    </tr>
                    <tr>
                        <td>Pool</td>
                        <td>Monthly Reward</td>
                        <td>Annual Reward</td>
                    </tr>
                    <tr>
                        <td>Chain 2</td>
                        <td>{this.state.chain2value}</td>
                    </tr>
                </tbody>
            </table>
        )
    }
}
