import logo from './logo.svg';
import './App.css';
import axios from 'axios';
import Web3 from 'web3';
import QuarkChain from 'quarkchain-web3';

const poolContractAddress = "0x006631C067D34928F55520511f01A5aBfA0AC67a";
const fullShardKey = "0003c66C"
const jrpcUrl = "http://jrpc.mainnet.quarkchain.io:38391";

let web3 = new Web3();
QuarkChain.injectWeb3(web3, jrpcUrl)

console.log(web3)

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <h2>QKC Chain3 staking pool</h2>
            <p>
                <table>
                    <tr>
                        <th>Function</th>
                        <th>Output</th>
                    </tr>
                    <tr>
                        <td>Total stakes</td>
                        <td>{getBalance()}</td>
                    </tr>
                    <tr>
                        <td>Miner fee</td>
                        <td id="minerFee"></td>
                    </tr>
                    <tr>
                        <td>Pool fee</td>
                        <td id="poolFee"></td>
                    </tr>
                    <tr>
                        <td>Minimum stake</td>
                        <td id="minStake"></td>
                    </tr>
                    <tr>
                        <td>Pool status</td>
                        <td id="status"></td>
                    </tr>
                    <tr>
                        <td>Deposit</td>
                        <td><input type="number" onChange={event } /></td>
                        <td id="deposit"></td>
                    </tr>
                </table>
            </p>
      </header>
    </div>
  );
}

function sendTransaction(address, value) {
  rawTx = {
    to: poolContractAddress,
    fromFullShardKey: "0x"+fullShardKey,
    toFullShardKey: "0x"+fullShardKey,
    gas: String(500000).toString("hex"),
    gasPrice: String
  }

}

function getBalance() {
  web3.qkc.getBalance(poolContractAddress + fullShardKey, function(err, res) {
    console.log(res.c[1])
    return res/10**18
  });
}

export default App;
