import logo from './logo.svg';
import './App.css';
import 'axios';
import Web3 from 'web3';
import QuarkChain from 'quarkchain-web3';

const poolContractAddress = "0x006631C067D34928F55520511f01A5aBfA0AC67a";
const fullShardKey = "00030000"
const jrpcUrl = "http://jrpc.mainnet.quarkchain.io:38391";

const ABIinterface = [{"inputs":[],"stateMutability":"nonpayable","type":"constructor"},{"inputs":[{"internalType":"uint256","name":"_minStake","type":"uint256"}],"name":"adjustMinStake","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"uint16","name":"amount","type":"uint16"}],"name":"adjustMinerFee","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"uint16","name":"amount","type":"uint16"}],"name":"adjustPoolFee","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"address payable","name":"_miner","type":"address"}],"name":"changeMiner","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"bool","name":"status","type":"bool"}],"name":"changePoolStatus","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[],"name":"flushContract","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"address","name":"staker","type":"address"}],"name":"getStakesWithRewards","outputs":[{"internalType":"uint256","name":"","type":"uint256"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"isClosed","outputs":[{"internalType":"bool","name":"","type":"bool"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"minStake","outputs":[{"internalType":"uint256","name":"","type":"uint256"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"miner","outputs":[{"internalType":"address payable","name":"","type":"address"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"minerFee","outputs":[{"internalType":"uint16","name":"","type":"uint16"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"owner","outputs":[{"internalType":"address payable","name":"","type":"address"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"poolFee","outputs":[{"internalType":"uint16","name":"","type":"uint16"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"totalStakes","outputs":[{"internalType":"uint256","name":"","type":"uint256"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"uint256","name":"amount","type":"uint256"}],"name":"withdrawStakes","outputs":[],"stateMutability":"nonpayable","type":"function"},{"stateMutability":"payable","type":"receive"}]

let web3 = new Web3();
QuarkChain.injectWeb3(web3, jrpcUrl)

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <h2>QKC Chain3 staking pool</h2>
            <p>
                <table>
                    <tr>
                        <th>Function</th>
                        <th>Output</th>
                    </tr>
                    <tr>
                        <td>Total stakes</td>
                        <td>{web3.qkc.getBalance(poolContractAddress + fullShardKey)}</td>
                    </tr>
                    <tr>
                        <td>Miner fee</td>
                        <td id="minerFee"></td>
                    </tr>
                    <tr>
                        <td>Pool fee</td>
                        <td id="poolFee"></td>
                    </tr>
                    <tr>
                        <td>Minimum stake</td>
                        <td id="minStake"></td>
                    </tr>
                    <tr>
                        <td>Pool status</td>
                        <td id="status"></td>
                    </tr>
                </table>
            </p>
      </header>
    </div>
  );
}

