import './App.css';
import axios from 'axios';
import Web3 from 'web3';
import QuarkChain from 'quarkchain-web3';
import Async from 'react-async';
import {ABIinterface, roiABIinterface, poolROIContractAddress, poolContractAddress, fullShardKey, jrpcUrl, standardShardKeys} from './components/config';
import {PoolInterface, PoolAllocateWithdraw} from './components/siteComponents.js'
import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

let address;

let web3 = new Web3()



if(window.ethereum){
  web3 = new Web3(web3.givenProvider);
  QuarkChain.injectWeb3(web3, jrpcUrl);
  window.ethereum.enable();
  address = web3.currentProvider.address;
} else {
  web3 = new Web3(web3.currentProvider);
}
console.log("test")

function App() {

  return (
      <div className="App" style={{backgroundColor:'#0C090A'}}>
        <nav class="navbar navbar-expand-lg navbar-dark bg-dark" style={{marginBottom:'25px'}}>
          <div className="container-fluid">

            <a className="navbar-brand" href="https://qkcstakingpools.xyz/">QuarkChain Staking Pools</a>
            <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
              <span class="navbar-toggler-icon"></span>
            </button>

            <div class="collapse navbar-collapse" id="navbarNav">
              <ul class="navbar-nav">
                <li class="nav-item">
                  <a class="nav-link" href="https://t.me/joinchat/ar58V6PuG5tkM2Y0" target="_blank" rel="noreferrer">Telegram</a>
                </li>
                <li class="nav-item">
                  <a class="nav-link" href="https://plakolm1006.medium.com/" target="_blank" rel="noreferrer">News</a>
                </li>
                <li class="nav-item">
                  <a class="nav-link" href="https://twitter.com/Quark_Chain" target="_blank" rel="noreferrer">Twitter</a>
                </li>
              </ul>
            </div>
          </div>
        </nav>
        <div className="container" style={{color:'white'}}>
          <PoolInterface poolAddress={poolContractAddress[2] + fullShardKey[2]} poolROIAddress={poolROIContractAddress[2]+fullShardKey[2]} index="2" miner="@marcthemauler" web3={web3} />
          <PoolInterface poolAddress={poolContractAddress[3] + fullShardKey[3]} poolROIAddress={poolROIContractAddress[3]+fullShardKey[3]} index="3" miner="@bukum86" web3={web3}/> 
        </div>
      </div>
  );
}

export default App;
