import axios from 'axios';
import React from 'react';

export default class ROICalculatorComp extends React.Component {

    state={
        chain2roi: '',
        chain3roi: '',
        chain7roi: '',

        chain2value: '',
        chain3value: '',
        chain7value: '',

        amount: 0,
    }

    componentDidMount() {
    }

    getROI() {
        axios.post("http://localhost:3001/api/getLatestROI", {chainId: 2}).then((response)=>{
            const data = response.data;
            this.setState({chain2roi: data.roi/100})
        });
        axios.post("http://localhost:3001/api/getLatestROI", {chainId: 3}).then((response)=>{
            const data = response.data;
            this.setState({chain3roi: data.roi/100})
        });
    }

    handleChange(event) {
        this.setState({chain2value: event.target.value*this.state.chain2roi, chain3value: event.target.value*this.state.chain3roi})
    }

    render() {
        return(
            <table>
                <tbody>
                    <tr>
                        <td colSpan="3">ROI Calculator</td>
                    </tr>
                    <tr>
                        <td colSpan="3"><input className="inp" onChange={e => this.handleChange(e)}/></td>
                    </tr>
                    <tr>
                        <td>Pool</td>
                        <td>Monthly Reward</td>
                        <td>Annual Reward</td>
                    </tr>
                    <tr>
                        <td>Chain 2</td>
                        <td>{this.state.chain2value}</td>
                    </tr>
                </tbody>
            </table>
        )
    }
}
