Mutability":"view","type":"function"},{"inputs":[],"name":"totalStakes","outputs":[{"internalType":"uint256","name":"","type":"uint256"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"uint256","name":"amount","type":"uint256"}],"name":"withdrawStakes","outputs":[],"stateMutability":"nonpayable","type":"function"},{"stateMutability":"payable","type":"receive"}]
const roiABIinterface = [{"inputs":[],"stateMutability":"nonpayable","type":"constructor"},{"inputs":[{"internalType":"uint16","name":"snapshotNumber","type":"uint16"}],"name":"calculateROI","outputs":[{"internalType":"uint256","name":"","type":"uint256"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"uint256","name":"","type":"uint256"}],"name":"snapshotBalance","outputs":[{"internalType":"uint256","name":"","type":"uint256"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"uint16","name":"snapshotNumber","type":"uint16"}],"name":"takeNewSnapshot","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[],"name":"timestamp1","outputs":[{"internalType":"uint256","name":"","type":"uint256"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"timestamp2","outputs":[{"internalType":"uint256","name":"","type":"uint256"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"timestamp3","outputs":[{"internalType":"uint256","name":"","type":"uint256"}],"stateMutability":"view","type":"function"}]
const poolROIContractAddress = ["0xd00204F286548579eaE01357B22996B44f4b0747", "0x035f71F9e800271A9e2A2ec2a45eEAFBb298293C"];
const poolContractAddress = ["0x0", "0x0","0x94f151BBD12854f93cf92c882B88E3790F407956", "0x006631C067D34928F55520511f01A5aBfA0AC67a", "0x0", "0x0", "0x0", "0x0"];
const fullShardKey = ["0000C66c", "0001C66c", "0002C66c", "0003C66c", "0004C66c", "0005C66c", "0006C66c", "0007C66c"];
const jrpcUrl = "http://jrpc.mainnet.quarkchain.io:38391";
const standardShardKeys = ["00000000", "00010000", "00020000", "00030000", "00040000", "00050000", "00060000", "00070000"]

let poolContract = [];
let roiContract = [];
let web3 = new Web3();
QuarkChain.injectWeb3(web3, jrpcUrl)

let minerFee = [];
let poolFee = [];

async function getContractInformation(poolAddress, poolROIAddress, index) {
  roiContract[index] = web3.qkc.contract(roiABIinterface).at(poolROIAddress);
  poolContract[index] = web3.qkc.contract(ABIinterface).at(poolAddress);

  let v1;
  let v2;
  let v3;
  let v4;
  let v5;
  let v6;
  let v7;

  web3.qkc.getBalance(poolAddress, function(err, res) {
    v1 = Number((res/10**18).toFixed(2)).toLocaleString() + " QKC";
  });

  poolContract[index].minerFee.call(function(err, res){
    minerFee[index] = res/10000
    v2 = res/100+"%";
  });
  poolContract[index].poolFee.call(function(err, res){
    poolFee[index] = res/10000
    v3 = res/100+"%";
  });
  poolContract[index].minStake.call(function(err, res){
    v4 = Number((res/10**18).toFixed(2)).toLocaleString() + " QKC";
  });

  poolContract[index].isClosed.call(function(err, res){
      if(res == false){
        v5 = "Open";
      }
      if(res == true){  
        v5 = "Closed";
      }
  });

  roiContract[index].calculateROI(1, {}, function(err, res) {

    let result = ((res/10**16)*(1-(minerFee[0] + poolFee[0]))).toFixed(3)

    v6 = result + "% / " + (result*12).toFixed(1) + "%";
  });

  if(getUserInformation.address == undefined) {
    v7 = "You need to ONLY have QPocket installed in order to check your stake"
  } else {
    poolContract[index].getStakesWithRewards(getUserInformation.address, {from: getUserInformation.address}, function(err, res) {
      v7 = Number((res/10**18).toFixed(2)).toLocaleString() + " QKC"
    });
  }

  return new Promise((resolve) => {
    setTimeout(() => {
      resolve({
        balance: v1,
        minerFee: v2,
        poolFee: v3,
        minStake: v4,
        status: v5,
        roi: v6,
        userStake: v7,
      })
    }, 1000)
  })
}

async function getUserInformation(web3) {
  let ad;
  let balance = [];

  if(web3.currentProvider.isQpocket){
    ad = await web3.currentProvider.address;
  } else {
    ad = await web3.eth.getAccounts()[0]
  }
  for(let i=0; i<8; i++) {
    balance[i] += await web3.qkc.getBalance(ad + standardShardKeys[i])
  }

  return new Promise((resolve) => {
    setTimeout(() => {
      resolve({
        address: ad,
        balance: balance,
      })
    }, 1000)
  })
}

